script:
frappe.ui.form.on("Shipment", {
    refresh(frm) {
        set_outer_box_filter(frm);
    },

    outer_boxes_add(frm) {
        set_outer_box_filter(frm);
    },

    outer_boxes_remove(frm) {
        set_outer_box_filter(frm);
    }
});

function set_outer_box_filter(frm) {
    frm.set_query("outer_box", "inner_boxes", function () {
        let outer_boxes = [];

        (frm.doc.outer_boxes || []).forEach(row => {
            if (row.name) {
                outer_boxes.push(row.name);
            }
        });

        return {
            filters: {
                name: ["in", outer_boxes]
            }
        };
    });
}
